import React, {useEffect, useState} from "react";
import {Link, useParams} from "react-router-dom";
import {ContactService} from "../../../services/ContactService";

let ViewContact = () => {

    let {contactId} = useParams();

    let [state , setState] = useState({
        loading: false,
        contact: {},
        errorMessage: ''
    });

    useEffect(() => {
        const fetchData = async () => {
            try{
                setState({...state, loading: true});
                let response = await ContactService.getContact(contactId);
                setState({
                    ...state,
                    loading: false,
                    contacts: response.data
                });
            }
            catch (error){
                setState({
                    ...state,
                    loading: false,
                    errorMessage: error.message
                });
            }
        };
        fetchData();
    }, [contactId]);

    let {loading , cotact , errorMessage} = state;
    return (
        <React.Fragment>
            <section className="view-contact-intro p-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-warning fw-bold">View Contact</p>
                            <p className="fst-italic">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ad commodi dolore id ipsam ipsum iste labore perspiciatis quasi tempore voluptate? Beatae incidunt iure nesciunt obcaecati perferendis praesentium, recusandae ullam voluptas?</p>
                        </div>
                    </div>
                </div>
            </section>
            <section className="view-contact mt-3">
                <div className="container">
                    <div className="row align-items-center">
                        <div className="col-md-4">
                            <img src="https://toppng.com/uploads/preview/icons-logos-emojis-user-icon-png-transparent-11563566676e32kbvynug.png" className="contact-img"/>
                        </div>
                        <div className="col-md-8">
                            <ul className="list-group">
                                <li className="list-group-item list-group-item-action">
                                    Name : <span className="fw-bold">Kai</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Moblie : <span className="fw-bold">090000000</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Email : <span className="fw-bold">Kai@gmail.com</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Company : <span className="fw-bold">Kai@gmail.com</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Title : <span className="fw-bold">Kai@gmail.com</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Group : <span className="fw-bold">Kai@gmail.com</span>
                                </li>
                            </ul>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <Link to={'/contacts/list'} className="btn btn-warning">Back</Link>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default ViewContact;
